import React, { Component } from 'react';
import './App.css';
import Counters from './Components/Counters';

class App extends Component {
  state = { 
    counters: [
      {id:1, value: 4},
      {id:2, value: 0},
      {id:3, value: 0},
      {id:4, value: 0},
    ]
  }
  render() { 
    return (
      <div className="App">
        <Counters />
        {this.state.counters.map( counter => (
          <h4> Counter { counter.value } </h4>
        ))};
      </div>
    );
  }
}

export default App;
